import React from 'react';
import friends from './friend'
import 'semantic-ui-css/semantic.min.css';
import Header from './Header'
import FriendList from './FriendList'
// import FriendForm from './FriendForm'
import {BrowserRouter, Route, Switch} from 'react-router-dom'
import Login from './Login'
import SignUp from './SignUp'



class App extends React.Component{

  constructor(){
    super()
    this.state = {
      testNew: "testnew",
      test: "test",
      form: false,
      friends: []
    }
  }

  addFriend = (e) => {
    e.preventDefault()

    let newFriend = {
      name: e.target[0].value,
      phone: e.target[1].value,
      email: e.target[2].value
    }

    let newArr = [...this.state.friends, newFriend]

    this.setState({
      friends: newArr
    })
  }

  getFriends = () => {
      fetch("http://localhost:3000/api/v1/friend",
      {
        method: "GET",
        headers: {
          Authorization: `Bearer ${localStorage.token}`
        }
      })
      .then(res => res.json())
      .then(console.log)
  }

  render(){

    return (

      <BrowserRouter>
    <div>
        <Header/>
        <button onClick={this.getFriends}>Show Friends</button>
        <Switch>

        <Route path="/login" component={Login} />

        <Route path="/signup" component={SignUp} />

        {/* <Route path="/paintings/new" render = {(routeProps) =><AddForm {...routeProps} add={this.addContact} /> } /> */}

        <Route path="/friends" render={() => <FriendList friendObjs={this.state.friends} />}/>

        </Switch>


    </div>
    </BrowserRouter>
  )};
}

export default App;
