import React, {Component} from 'react'

class FriendCard extends Component{

  state = {
    showFriend: false
  }


  handleShowFriend = () => {
    this.state.showFriend?
    this.setState({
      showFriend: false
    })
    : this.setState({
      showFriend: true
    })
  }

  render(){
    let displayFriend = this.props
    // console.log(displayFriend)
    return (
      <div>
        <div class="ui celled list">
        <div class="item">
          <div class="content">
            <div class="header"> {displayFriend.name} </div>
            {displayFriend.phone}       
            {displayFriend.email}       
            Next Appointment:   
            <button onClick={()=>this.handleShowFriend()}>Edit Friend</button>
            {this.state.showFriend ? <EditFriend onSubmit={this.createPoem}/> : null } 
          </div>
        </div>
        </div>


      </div>
    );
  };
}
export default FriendCard
